let deckId,str;function fetchApi(){fetch("https://www.deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1").then((e=>e.json())).then((e=>deckId=e.deck_id))}function drawCard(){fetch(`https://www.deckofcardsapi.com/api/deck/${deckId}/draw/?count=2`).then((e=>e.json())).then((e=>{console.log(e.cards),document.getElementById("cards").innerHTML=`<img src='${e.cards[0].image}'>\n                                                 <img src='${e.cards[1].image}\n                                                 '>\n                                                 <p>Computer:</p>\n                                                 <p>Me:</p>`}))}function renderCards(){document.getElementById("cards").innerHTML=str}document.getElementById("btn").addEventListener("click",fetchApi),document.getElementById("drawBtn").addEventListener("click",(()=>{deckId||fetchApi(),drawCard()}));